package org.gitor.autodeployoss.controller;import org.gitor.autodeployoss.entity.ParamsPojo;import org.gitor.autodeployoss.service.DeployService;import org.gitor.autodeployoss.service.ServiceFactory;import org.gitor.autodeployoss.utils.PropertiesUtils;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @author : liuwenlong * @date : 2018/5/30 18:26 */@RestController@RequestMapping("/")public class DeployController {    /**     * 通过自主传参数进行自动部署     * @param paramsPojo 参数列表     * @return 部署结果     */    @GetMapping(value = "/deployByParams")    public Object deployByParams(ParamsPojo paramsPojo){        DeployService deployService = ServiceFactory.getService(paramsPojo.getOssType());        deployService.excute(paramsPojo);        return "暂时请使用配置文件的方式进行调用";    }    /**     * 通过配置文件进行自动部署     * @param key 配置文件中的key值     * @return 部署结果     */    @GetMapping(value = "/deployByProperties")    public Object deployByProperties(String key){        ParamsPojo paramsPojo = PropertiesUtils.getParamsPojoFromProperties(key);        DeployService deployService = ServiceFactory.getService(paramsPojo.getOssType());        deployService.excute(paramsPojo);        return "ok";    }}