package org.gitor.autodeployoss.entity;import javax.persistence.*;import java.util.Date;/** * @author : liuwenlong * @date : 2018/6/4 14:54 */@Entitypublic class LogEntity {    /**     * 创建时间     */    private Date createTime;    /**     * 日志内容     */    @Lob    private String msg;    /**     * 状态，成功为true，失败为false     */    private boolean status;    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    private LogEntity(String msg,boolean status){        this.createTime = new Date();        this.msg = msg;        this.status = status;    }    public static LogEntity errorLog(String msg){        return new LogEntity(msg,false);    }    public static LogEntity successLog(String msg){        return new LogEntity(msg,true);    }    public Date getCreateTime() {        return createTime;    }    public void setCreateTime(Date createTime) {        this.createTime = createTime;    }    public String getMsg() {        return msg;    }    public void setMsg(String msg) {        this.msg = msg;    }    public boolean isStatus() {        return status;    }    public void setStatus(boolean status) {        this.status = status;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    @Override    public String toString() {        return "LogEntity{" +                "createTime='" + createTime + '\'' +                ", msg='" + msg + '\'' +                ", status=" + status +                ", id=" + id +                '}';    }}