package org.gitor.autodeployoss.service;import org.gitor.autodeployoss.utils.Contans;import org.gitor.autodeployoss.utils.SpringContextUtils;/** * service 工厂，目前仅支持阿里云，但是预留了其他oss的空间 * 后续扩展到其他的oss * @author : liuwenlong * @date : 2018/6/4 15:46 */public class ServiceFactory {    /**     * 获取实现类     * @param type oss 类型，参照Contans     * @return     */    public static DeployService getService(int type){        DeployService deployService = null;        if (type == Contans.ossType.ALIYUN.value()){            deployService = SpringContextUtils.getBean("aliyunOSSServiceImpl",DeployService.class);        }else if(type == Contans.ossType.QINIUYUN.value()){            deployService = SpringContextUtils.getBean("aliyunOSSServiceImpl",DeployService.class);        }else if (type == Contans.ossType.TENGXUNYUN.value()){            deployService = SpringContextUtils.getBean("aliyunOSSServiceImpl",DeployService.class);        }else if (type == Contans.ossType.ZHIXIAOYUN.value()){            deployService = SpringContextUtils.getBean("aliyunOSSServiceImpl",DeployService.class);        }        return deployService;    }}