package org.gitor.autodeployoss.service.impl;import com.aliyun.oss.OSSClient;import org.gitor.autodeployoss.entity.LogEntity;import org.gitor.autodeployoss.service.AbstractOSSService;import org.gitor.autodeployoss.service.DeployService;import org.springframework.stereotype.Service;import java.io.File;/** * @author : liuwenlong * @date : 2018/5/31 22:29 */@Service(value = "aliyunOSSServiceImpl")public class AliyunOSSServiceImpl extends AbstractOSSService implements DeployService{    private OSSClient ossClient;    public AliyunOSSServiceImpl(){        ossClient = new OSSClient(super.paramsPojo.getEndpoint(),                super.paramsPojo.getAccessKeyId(),                super.paramsPojo.getAccessKeySecret());    }    @Override    protected void deleteOSS() {        super.filenames.forEach(l -> {            System.out.println(l);            ossClient.putObject(super.paramsPojo.getBucketName(),                    l.replace("/Users/liuwenlong/Downloads/test/resume/",""), new File(l));        });        logRepository.save(LogEntity.successLog("删除成功！！！"));    }    @Override    protected void uploadOSS() {        logRepository.save(LogEntity.successLog("上传成功"));    }}